    generateCards(){
        let i = 0
        let wordArr = []
        this.state.words.sort(() => 0.5 - Math.random()).map(word => {
            while (i < 25){
                i++
                return (
                    wordArr.push(word)
                )
            }
            console.log(wordArr)
        })
    }




