import React from 'react'
import Card from '../component/Card'

class GameBoard extends React.Component{

    state = {
        words: [],
        loading: true,
        displayedWords: []
    }

    componentDidMount() {
    fetch("https://codename-backend.herokuapp.com/cards/index")
      .then(response => response.json())
      .then(word => this.setState({words:word,loading:false}))
    }

    generateCards(){
        let i = 1
        return this.state.words.sort(() => 0.5 - Math.random()).map(word => {
            while (i < 26){
                i++
                this.setState({displayedWords:word})
            }
        })
    }






    render(){


     return (
         <div className = "container">
         {this.generateCards()}
         </div>
     )



  }
}
export default GameBoard;
