import Card from '../component/Card'

class GameBoard extends React.Component{

    state = {
        words: [],
        loading: true,
        bgColor: [
            'red',
            'red',
            'red',
            'red',
            'red',
            'red',
            'red',
            'red',
            'red',
            'blue',
            'blue',
