            'blue',
            'green',
            'green',
            'green',
            'green',
            'green',
            'green',
            'green',
            'green',
            'black'
        ],
        chosenWords:[]
    }

    componentDidMount() {
    fetch("https://codename-backend.herokuapp.com/cards/index")
      .then(response => response.json())
      .then(words => this.setState({words,loading:false}))
      .then(this.generateCards)
    }

    generateCards(){
        let i = 0
        return this.state.words.sort(() => 0.5 - Math.random()).map(word => {
            while (i < 25){
                i++
                this.setState({ chosenWords: [...this.state.chosenWords,word] })

            }
        })
    }


    render(){
        console.log(this.state.chosenWords)
     return (
         <div className = "container">

         </div>
     )



  }
}
export default GameBoard;
