import React from 'react'
import CardFront from './CardFront'
import CardBack from './CardBack'

class Card extends React.Component{

    state = {
        isFront: true,
        bgColor:""
    }

    clickHandler = () =>{
        this.setState({isFront: !this.state.isFront})
        this.setState({bgColor:"red"})


    }


    render(){

        console.log(this.props)

        return(
            <div className = "wordCard" style={{backgroundColor: this.state.bgColor}} id = {this.props.word.id} onClick = {this.clickHandler}>
            {this.state.isFront ? <CardFront front = {this.props.word.word}/ > : <CardBack/>}
            </div>
        )

    }

}
export default Card;
